import { useEffect, useState } from "react";

const SimpleInput = (props) => {

  const [enteredName, setEnteredName] = useState("");
  const [enteredNameisValid, setEnteredNameisValid] = useState(false);
  const [enteredNameWasTouched, setenteredNameWasTouched] = useState();

  const nameChangehandler = (event) => {
    setEnteredName(event.target.value)
  }
  useEffect(() => {
    if(enteredNameisValid){
      console.log("Name Input is valid")
    }
  },[enteredNameisValid])

  const submitHandler = (event) => {
    event.preventDefault();
    if(enteredName.trim() ==='') {
      setEnteredNameisValid(false);
      return;
    }
    setEnteredNameisValid(true);
    console.log(enteredName);
  }
  let nameInputClasses =  enteredNameisValid ? 'form-control': 'form-control invalid'

  return (
    <form onSubmit={submitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={nameChangehandler} value={enteredName}/>
        {!enteredNameisValid && <p className="error-text">Name should not be empty.</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
