import { useReducer, useState } from "react";


const iniitalInputState = {
    value: "",
    isToouched: false
}

const inputStateReducer = (state, action) => {

    if(action.type === "INPUT") {

    }
    if(action.type === "BLUR") {

    }
    if(action.type === "REMOVE") {

    }
    return iniitalInputState;
}


const useInput = (validate) => {

    const [inputState, dispathInputValueFn] = useReducer(inputStateReducer, iniitalInputState)

    const valueIsValid = validate(inputState.value);
    const hasError =  inputState.isToouched && !valueIsValid;
    const valueChangeHandler = (event) => {
        setValue(event.target.value);

    }
    const valueBlurHandler = () => {
        setIsTouched(true)
    }
    const reset = () => {
        setValue("");
        setIsTouched(false);
    }

    return {
        value,
        hasError,
        valueChangeHandler,
        valueBlurHandler,
        isValid: valueIsValid,
        reset
    }

}

export default useInput;
