import { useState } from "react";
import useInput from "../Hooks/useInput";

const SimpleInput = () => {

  const {value, hasError, isTouched, valueChangeHandler, valueBlurHandler} = useInput();

  const [enteredName, setEnteredName] = useState("");
  const [enteredNameTouched, setenteredNameTouched] = useState(false);


  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredEmailTouched, setenteredEmailTouched] = useState(false);

  const enteredNameisValid = enteredName.trim() !== '';
  const enteredEmailisValid = enteredEmail.includes('@') && enteredEmail.trim()  !== '';

  const nameInputIsInvalid = !enteredNameisValid && enteredNameTouched;
  const emailInputIsInvalid = !enteredEmailisValid && enteredEmailTouched;

  let formIsValid = false;
  if(enteredNameisValid && enteredEmailisValid) {
    formIsValid = true;
  }

  const nameChangehandler = (event) => {
    setEnteredName(event.target.value);
  }
  const emailChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  }

  const submitHandler = (event) => {
    event.preventDefault();
    setenteredNameTouched(true);
    if(!enteredNameisValid) {
      return;
    }
    setEnteredName('');
    setenteredNameTouched(false);

    setEnteredEmail('');
    setenteredEmailTouched(false);
  }

  const nameInputBlurHandler = () => {
    setenteredNameTouched(true);
  }

  const emailInputBlurHandler = () => {
    setenteredEmailTouched(true);
  }

  let nameInputClasses =  nameInputIsInvalid ? 'form-control invalid': 'form-control';
  let emailInputClasses = emailInputIsInvalid ? 'form-control invalid' : 'form-control';

  return (
    <form onSubmit={submitHandler}>
      <div className={nameInputClasses}>
          <label htmlFor='name'>Your Name</label>
          <input type='text' id='name' onChange={nameChangehandler} value={enteredName} onBlur={nameInputBlurHandler}/>
          {nameInputIsInvalid && <div><span className="error-text">Name should not be empty.</span> </div>}
      </div>
      <div className={emailInputClasses}>
          <label htmlFor='email'>Your Email</label>
          <input type='email' id='email' value={enteredEmail} onChange={emailChangeHandler} onBlur={emailInputBlurHandler}/>
          {emailInputIsInvalid && <div><span className="error-text">Please enter a valid email</span> </div>}
      </div>
      <div className="form-actions">
          <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
