import { useState } from "react";

const SimpleInput = () => {

  const [enteredName, setEnteredName] = useState("");
  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredNameTouched, setenteredNameTouched] = useState(false);

  const enteredNameisValid = enteredName.trim() !== '';
  const enteredEmailisValid = enteredEmail.includes('@');
  const nameInputIsInvalid = !enteredNameisValid &&  !enteredEmailisValid && enteredNameTouched;

  let formIsValid = false;
  if(enteredNameisValid) {
    formIsValid = true;
  }

  const nameChangehandler = (event) => {
    setEnteredName(event.target.value)
  }
  const emailChangeHandler = (event) => {
    setEnteredEmail(event.target.value)
  }

  const submitHandler = (event) => {
    event.preventDefault();
    setenteredNameTouched(true);
    if(!enteredNameisValid) {
      return;
    }
    setEnteredName('');
    setenteredNameTouched(false);
  }

  const nameInputBlurHandler = () => {
    setenteredNameTouched(true);
  }

  let InputClasses =  nameInputIsInvalid ? 'form-control invalid': 'form-control'

  return (
    <form onSubmit={submitHandler}>
      <div className={InputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={nameChangehandler} value={enteredName} onBlur={nameInputBlurHandler}/>
        {nameInputIsInvalid && <div><span className="error-text">Name should not be empty.</span> </div>}
        </div>
        <div className={InputClasses}>
        <label htmlFor='name'>Your Email</label>
        <input type='email' id='email' value={enteredEmail} onChange={emailChangeHandler}/>
        {nameInputIsInvalid && <div><span className="error-text">Email should not be empty.</span> </div>}
        </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
