import { useState } from "react";

const BasicForm = (props) => {

  const [firstName, setFirstName] = useState("");
  const [fNameBlur, setFnameBlur] = useState(false);

  const [lastName, setLastName] = useState("");
  const [lNameBlur, setlnameBlur] = useState(false);

  const [email,setEmail] = useState("");
  const [emailBlur, setEmailBlur] = useState(false);

  const firstNameIsValid = firstName.trim() !== '';
  const firstnameInputIsInValid = !firstNameIsValid && fNameBlur;

  const lastNameIsValid = lastName.trim() !== '';
  const lnameInputIsValid = !lastNameIsValid && lNameBlur;

  const emailIsValid = email.trim() !== '' && email.includes('@');
  const emailInputIsValid = !emailIsValid && emailBlur;

  let formIsValid = false;

  if(firstnameInputIsInValid && lnameInputIsValid && emailInputIsValid) {
    formIsValid = true;
  }

  let nameInputClass = firstnameInputIsInValid ? 'form-control invalid'  : 'form-control'

  const fNameHandler = (event) => {
    setFirstName(event.target.value);
  }

  const fNameBlurhandler = () => {
    setFnameBlur(true);
  }

  const lNameHandler = (event) => {
    setLastName(event.target.value);
  }

  const lNameBlurhandler = () =>{
    setlnameBlur(true);
  }

  const emailHandler = (event) => {
    setEmail(event.target.value);

  }

  const emailBlurHandler = () => {
    setEmailBlur(true);

  }

  const submitHandler = (event) => {
    setEmailBlur(true);
    setlnameBlur(true);
    setFnameBlur(true);
    event.preventDefault();
  }

  return (
    <form onSubmit={submitHandler}>
      <div className='control-group'>
        <div className={nameInputClass}>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' value={firstName} onChange={fNameHandler} onBlur={fNameBlurhandler}/>
          { <div> <p className="error-text">First Name sould not be empty</p> </div>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' value={lastName} onChange={lNameHandler} onBlur={lNameBlurhandler}/>
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' value={email} onChange={emailHandler} onBlur={emailBlurHandler}/>
      </div>
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
