import { useEffect, useState } from "react";

const SimpleInput = (props) => {

  const [enteredName, setEnteredName] = useState("");
  const [enteredNameisValid, setEnteredNameisValid] = useState(false); 
  const [enteredNameTouched, setenteredNameTouched] = useState(false); 

  const nameChangehandler = (event) => {
    setEnteredName(event.target.value)

    if(event.target.value.trim().length !== 0 ) {
      setEnteredNameisValid(true);
    }
  }

  useEffect(() => {
    if(enteredNameisValid){
      console.log("Name Input is valid")
    }
  },[enteredNameisValid])

  const submitHandler = (event) => {
    event.preventDefault();
    setenteredNameTouched(true);
    if(enteredName.trim() ==='') {
      setEnteredNameisValid(false);
      return;
    }
    setEnteredNameisValid(true);
    console.log(enteredName);
  }

  const nameInputBlurHandler = (event) => {
    setenteredNameTouched(true);
    if(enteredName.trim() ==='') {
      setEnteredNameisValid(false);
    }
  }

  const nameInputIsInvalid = !enteredNameisValid && enteredNameTouched;
  let nameInputClasses =  nameInputIsInvalid ? 'form-control invalid': 'form-control'

  return (
    <form onSubmit={submitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={nameChangehandler} value={enteredName} onBlur={nameInputBlurHandler}/>
        {nameInputIsInvalid && <p className="error-text">Name should not be empty.</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
