import { useState } from "react";

const BasicForm = (props) => {

  const [firstName, setFirstName] = useState("");
  const [fNameBlur, setFnameBlur] = useState(false);

  const [lastName, setLastName] = useState("");
  const [lNameBlur, setlnameBlur] = useState(false);

  const [email,setEmail] = useState("");
  const [emailBlur, setEmailBlur] = useState(false);

  const firstNameIsValid = firstName.trim() !== '';
  const firstnameInputIsValid = !firstNameIsValid && fNameBlur;

  const fNameHandler = (event) => {
    console.log(event.target.value);
    setFirstName(event.target.value);
  }

  const fNameBlurhandler = () => {
    setFnameBlur(true);
  }

  const lNameHandler = (event) => {
    setLastName(event.target.value);
  }

  const lNameBlurhandler = () =>{
  }

  const emailHandler = (event) => {
    setEmail(event.target.value);

  }

  const emailBlurHandler = () => {

  }

  const submitHandler = (event) => {
    setEmailBlur(true);
    setlnameBlur(true);
    setFnameBlur(true);
    event.preventDefault();
  }

  return (
    <form onSubmit={submitHandler}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' value={firstName} onChange={fNameHandler} onBlur={fNameBlurhandler}/>
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' value={lastName} onChange={lNameHandler} onBlur={lNameBlurhandler}/>
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' value={email} onChange={emailHandler} onBlur={emailBlurHandler}/>
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
