import { useState } from "react";
import useInput from "../Hooks/useInput";

const BasicForm = () => {

   const {value: fName, 
    isValid: fNameIsValid, 
    valueBlurHandler: fNameBlurhandler, 
    valueChangeHandler: fNameHandler, 
    hasError: fNameError, reset: resetfName} = useInput(fName => fName.trim() !== '');

    const {
      value:lName,
      isValid: lnameIsValid,
      valueBlurHandler:lnameBlurhandler,
      valueChangeHandler: lnameHandler,
      hasError: lNameError, reset: resetlName   } =useInput(lName => lName.trim() !== '');


  const [lastName, setLastName] = useState("");
  const [lNameBlur, setlnameBlur] = useState(false);

  const [email,setEmail] = useState("");
  const [emailBlur, setEmailBlur] = useState(false);


  const lastNameIsValid = lastName.trim() !== '';
  const lnameInputIsInvalid = !lastNameIsValid && lNameBlur;

  const emailIsValid = email.trim() !== '' && email.includes('@');
  const emailInputIsInvalid = !emailIsValid && emailBlur;

  let formIsValid = false;
  if(fNameIsValid && lastNameIsValid && emailIsValid) {
    formIsValid = true;
  }

  let nameInputClass = fNameError ? 'form-control invalid'  : 'form-control';
  let lnameInputClass = lnameInputIsInvalid ? 'form-control invalid'  : 'form-control'
  let emailInputClass = emailInputIsInvalid ? 'form-control invalid'  : 'form-control'

  const lNameHandler = (event) => {
    setLastName(event.target.value);
  }

  const lNameBlurhandler = () =>{
    setlnameBlur(true);
  }

  const emailHandler = (event) => {
    setEmail(event.target.value);

  }

  const emailBlurHandler = () => {
    setEmailBlur(true);

  }

  const submitHandler = (event) => {
    event.preventDefault();
    resetfName();


    setEmailBlur(false);
    setlnameBlur(false);
    setLastName("");
    setEmail("");


  }

  return (
    <form onSubmit={submitHandler}>
      <div className='control-group'>
        <div className={nameInputClass}>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' value={fName} onChange={fNameHandler} onBlur={fNameBlurhandler}/>
          {fNameError && <div> <span className="error-text">First Name Should Not Be Empty</span> </div>}
        </div>
        <div className={lnameInputClass}>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' value={lName} onChange={lnameHandler} onBlur={lnameBlurhandler}/>
          {lnameInputIsInvalid && <div> <span className="error-text">Last Name Should Not Be Empty</span> </div>}
        </div>
      </div>
      <div className={emailInputClass}>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' value={email} onChange={emailHandler} onBlur={emailBlurHandler}/>
        {emailInputIsInvalid && <div> <span className="error-text">Please Enter A Valid Email</span> </div>}
      </div>
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
